&lt;head&gt;
    &lt;style&gt;
     div {
    animation-name: example;
    position: relative;
    width: 150px;
    height: 150px;
    background-color: black;
    animation-duration: 4s;
    animation-iteration-count: infinite;
}

@keyframes example {
  0%   {background-color: purple; left:0px; top:0px;}
  25%  {background-color: blue; left:0px; top:200px;}
  50%  {background-color: purple; left:200px; top:200px;}
  75%  {background-color: blue; left:200px; top:0px;}
  100% {background-color: purple; left:0px; top:0px;}
}
&lt;/style&gt;
&lt;/head&gt;
